/* Function block execution function */
void DigitalControllerrun(DigitalController* me)
{
    me->_output.events = 0;

    if (me->_input.event.Update) {
        me->x = me->_x;
        me->y = me->_y;
    }
    for (;;) {
        switch (me->_state) {
            case 0:
                // State: Start
                if (!me->_entered) {
                    me->_entered = true;
                }
                else {
                    if (me->x <= 20) {
                        me->_state = 1;
                        me->_entered = false;
                        continue;
                    }
                    else if (me->x >= 100) {
                        me->_state = 2;
                        me->_entered = false;
                        continue;
                    }
                }
                break;
            case 1:
                // State: State2
                if (!me->_entered) {
                    DigitalController_TurnOn(me);
                    me->_output.event.TurnOn = 1;
                    me->_entered = true;
                }
                else {
                    me->_state = 0;
                    me->_entered = false;
                    continue;
                }
                break;
            case 2:
                // State: State3
                if (!me->_entered) {
                    DigitalController_TurnOff(me);
                    me->_output.event.TurnOff = 1;
                    me->_entered = true;
                }
                else {
                    me->_state = 0;
                    me->_entered = false;
                    continue;
                }
                break;
        }
        break;
    }

    me->_input.events = 0;
}

